local monitor = peripheral.find("monitor")
monitor.setTextScale(0.5)

local startTime = 0

while true do
    startTime = os.clock()
    os.sleep(0.05)
    local imageData = ""
    local imageDataSharpened = ""

    local monitorWidth, monitorHeight = monitor.getSize()
    local monitorRenderString = string.rep("#", monitorWidth)

    monitor.setPaletteColor(colors.white, 0.976, 0.98, 0.98)
    monitor.setPaletteColor(colors.orange, 0.918, 0.914, 0.91)
    monitor.setPaletteColor(colors.magenta, 0.82, 0.804, 0.804)
    monitor.setPaletteColor(colors.lightBlue, 0.776, 0.749, 0.741)
    monitor.setPaletteColor(colors.yellow, 0.765, 0.706, 0.667)
    monitor.setPaletteColor(colors.lime, 0.765, 0.655, 0.549)
    monitor.setPaletteColor(colors.pink, 0.694, 0.682, 0.686)
    monitor.setPaletteColor(colors.gray, 0.651, 0.627, 0.624)
    monitor.setPaletteColor(colors.lightGray, 0.639, 0.529, 0.475)
    monitor.setPaletteColor(colors.cyan, 0.541, 0.522, 0.518)
    monitor.setPaletteColor(colors.purple, 0.49, 0.376, 0.357)
    monitor.setPaletteColor(colors.blue, 0.325, 0.29, 0.275)
    monitor.setPaletteColor(colors.brown, 0.863, 0.188, 0.133)
    monitor.setPaletteColor(colors.green, 0.706, 0.157, 0.129)
    monitor.setPaletteColor(colors.red, 0.451, 0.137, 0.125)
    monitor.setPaletteColor(colors.black, 0.173, 0.133, 0.122)

    monitor.clear()

    for y = 0, monitorHeight do
        local stringSegmentStart = (y * monitorWidth) + 1
        local stringSegmentEnd = ((y + 1) * monitorWidth)
        local backgroundString = string.sub(imageData, stringSegmentStart, stringSegmentEnd)
        local foregroundString = string.sub(imageDataSharpened, stringSegmentStart, stringSegmentEnd)
        monitor.setCursorPos(1, y)
        monitor.blit(monitorRenderString, foregroundString, backgroundString)
    end
    print("Took " .. tostring((os.clock() - startTime) * 1000) .. "ms")

    os.queueEvent("randomEvent")
    os.pullEvent()

end