local imageData = ""
local imageDataSharpened = ""
local monitor = peripheral.find("monitor")
monitor.setTextScale(0.5)
local monitorWidth, monitorHeight = monitor.getSize()
local monitorRenderString = string.rep("#", monitorWidth)

monitor.setPaletteColor(colors.white, 0.82, 0.835, 0.306)
monitor.setPaletteColor(colors.orange, 0.69, 0.773, 0.267)
monitor.setPaletteColor(colors.magenta, 0.592, 0.682, 0.224)
monitor.setPaletteColor(colors.lightBlue, 0.463, 0.588, 0.173)
monitor.setPaletteColor(colors.yellow, 0.404, 0.49, 0.216)
monitor.setPaletteColor(colors.lime, 0.345, 0.455, 0.145)
monitor.setPaletteColor(colors.pink, 0.341, 0.4, 0.255) 
monitor.setPaletteColor(colors.gray, 0.29, 0.392, 0.133)
monitor.setPaletteColor(colors.lightGray, 0.286, 0.357, 0.192)
monitor.setPaletteColor(colors.cyan, 0.267, 0.314, 0.231)
monitor.setPaletteColor(colors.purple, 0.224, 0.318, 0.102)
monitor.setPaletteColor(colors.blue, 0.204, 0.278, 0.122)
monitor.setPaletteColor(colors.brown, 0.184, 0.243, 0.122)
monitor.setPaletteColor(colors.green, 0.149, 0.224, 0.071)
monitor.setPaletteColor(colors.red, 0.133, 0.188, 0.082)
monitor.setPaletteColor(colors.black, 0.098, 0.137, 0.051)

monitor.clear()

for y = 0, monitorHeight do
    
    local stringSegmentStart = (y * monitorWidth) + 1
    local stringSegmentEnd = ((y + 1) * monitorWidth)

    local backgroundString = string.sub(imageData, stringSegmentStart, stringSegmentEnd)
    local foregroundString = string.sub(imageDataSharpened, stringSegmentStart, stringSegmentEnd)
    print(tostring(stringSegmentStart) .. " : " .. tostring(stringSegmentEnd))
    monitor.setCursorPos(1, y)
    monitor.blit(monitorRenderString, foregroundString, backgroundString)
    
 end